create schema DBBestBuy;
use DBBestBuy;
create table THANHVIEN
(
	MATV VARCHAR(20) PRIMARY KEY,
	HOTEN varchar(100),
	EMAIL VARCHAR(30),
	ZIPCODE VARCHAR(7),
	SODIENTHOAI VARCHAR(16)
);

CREATE TABLE TAIKHOAN_DANGNHAP
(
	USERNAME VARCHAR(20) PRIMARY KEY,
	PASSWORD VARCHAR(20),
	ROLES varchar(12) CHECK (ROLES IN ('ADMIN','KHACHHANG')),
	MATV VARCHAR(20),
	FOREIGN KEY(MATV) REFERENCES THANHVIEN(MATV)
);

CREATE TABLE CTKHUYENMAI
(
	MACT VARCHAR(20) PRIMARY KEY,
	NGAYBATDAU DATE,
	NGAYKETTHUC DATE,
	THONGTINCHUNG varchar(4000)
);

CREATE TABLE KHUYENMAI
(
	MAKM VARCHAR(20) PRIMARY KEY,
	NGAYBATDAU DATE,
	NGAYKETTHUC DATE,
	GIAKM FLOAT,
	THONGTINKHUYENMAI varchar(4000),
	CTKMAI VARCHAR(20),
	FOREIGN KEY(CTKMAI) REFERENCES CTKHUYENMAI(MACT)
);

CREATE TABLE HANGSANXUAT
(
	MAHSX VARCHAR(20) PRIMARY KEY,
	TENHSX varchar(50)
);

CREATE TABLE LOAISANPHAM
(
	MALOAI VARCHAR(20) PRIMARY KEY,
	TENLOAI varchar(50)
);
CREATE TABLE SANPHAM
(
	MASP VARCHAR(20) PRIMARY KEY,
	TENSP varchar(200),
	LOAISP VARCHAR(20),
	GIA FLOAT,
	SOLUONGCON INT,
	HINHANH VARCHAR(50),
	LASPNOIBAT BIT,
	HANGSX VARCHAR(20),
	KHUYENMAI VARCHAR(20),
	DIEM FLOAT,
	FOREIGN KEY(HANGSX) REFERENCES HANGSANXUAT(MAHSX),
	FOREIGN KEY(KHUYENMAI) REFERENCES KHUYENMAI(MAKM),
	FOREIGN KEY(LOAISP) REFERENCES LOAISANPHAM(MALOAI)
);

create table PHUKIENKEMTHEO
(
	MASP varchar(20),
	MAPK varchar(20),
	primary key (MASP, MAPK),
	foreign key (MASP) references SANPHAM(MASP),
	foreign key (MAPK) references SANPHAM(MASP)
);


CREATE TABLE CHITIETKYTHUAT
(
	MASP VARCHAR(20),
	DATDIEM VARCHAR(100),
	CHITIET VARCHAR(500),
	MOTA VARCHAR(4000),
	PRIMARY KEY(MASP,DATDIEM),
	FOREIGN KEY(MASP) REFERENCES SANPHAM(MASP)
);

CREATE TABLE NHANXET
(
	MANX VARCHAR(20),
	NGUOINX VARCHAR(20),
	SANPHAM VARCHAR(20),
	THOIGIAN DATETIME,
	NOIDUNG varchar(4000),
	DIEMSP INT,
	
	FOREIGN KEY(NGUOINX) REFERENCES THANHVIEN(MATV),
	FOREIGN KEY(SANPHAM) REFERENCES SANPHAM(MASP)
);

CREATE TABLE PHIEU_DAT_HANG
(
	MAPD VARCHAR(20) PRIMARY KEY,
	THOIGIANDAT DATETIME,
	TONGGIA FLOAT,
	TRANGTHAI VARCHAR(7) CHECK (TRANGTHAI IN ('DATHANHTOAN','CHUATHANHTOAN')),
	NGUOIDAT VARCHAR(20),
	FOREIGN KEY(NGUOIDAT) REFERENCES THANHVIEN(MATV)
);

CREATE TABLE CHITIETPHIEUDAT
(
	MACTD VARCHAR(20) PRIMARY KEY,
	MASP VARCHAR(20),
	SLDAT INT,
	MAPD VARCHAR(20),
	GHICHU varchar(200),
	FOREIGN KEY(MASP) REFERENCES SANPHAM(MASP),
	FOREIGN KEY(MAPD) REFERENCES PHIEU_DAT_HANG(MAPD)
);

